  ) where

import FormEngine.FormItem

formItems :: [FormItem]
formItems = prepareForm [Chapter {chDescriptor = FIDescriptor {iLabel = Just "Design of experiment", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Just "chapter", iLongDescription = Just "Before you decide to embark on any new study, it is nowadays good practice to consider all options to keep the data generation part of your study as limited as possible. It is not because we can generate massive amounts of data that we always need to do so. Creating data with public money is bringing with it the responsibility to treat those data well and (if potentially useful) make them available for re-use by others.", iLink = Nothing, iMandatory = False, iRules = []}, chItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Is there any pre-existing data?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Are there any data sets available in the world that are relevant to your planned research?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [DetailedOption NoNumbering "No" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [InfoFI {ifiDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, ifiText = "You know that this is very unlikely? This question is not only about data sets that are similar to what you want to determine yourself, but also reference data or data that should be mined from the existing literature. Further, it is very likely that you will refer to related data, e.g. other databases where you usually \"quickly look something up\", but that could maybe be properly integrated, especially if you need to do such lookups multiple times."}]}],DetailedOption NoNumbering "Yes" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you be using any pre-existing data (including other people's data)?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Will you be referring to any earlier measured data, reference data, or data that should be mined from existing literature? Your own data as well as data from others?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [DetailedOption NoNumbering "No" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [InfoFI {ifiDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, ifiText = "Did you research all the data that exists? You may not be aware of all existing data that could be available. Although using and/or integrating existing data sets may pose a challenge, it will normally be cheaper than collecting everything yourself. Even if you decide not to use an existing data set, it is better to do this as a conscious decision."}]}],DetailedOption NoNumbering "Yes" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Just "What reference data will you use?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Much of todays data is used in comparison with reference data. A genome for instance is compared with a reference genome to identify genomic variants. If you use reference data, there are several other issues that you should consider. What are the reference data sets that you will use?</p>", iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [TextFI {tfiDescriptor = FIDescriptor {iLabel = Just "Items", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Just "Write each item on new line", iLongDescription = Just "<p class=\"question-description\">Much of todays data is used in comparison with reference data. A genome for instance is compared with a reference genome to identify genomic variants. If you use reference data, there are several other issues that you should consider. What are the reference data sets that you will use?</p>", iLink = Nothing, iMandatory = False, iRules = []}}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Where is it available?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Do you know where the reference data is available, what the conditions for use are, and how to reference it?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [DetailedOption NoNumbering "No" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [InfoFI {ifiDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, ifiText = "Figure this out quickly!"}]}],SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "In what format is the reference data available?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Do you know the data format of the reference data? Is this suitable for your work? Does it need to be converted?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "I can directly use it",SimpleOption "I need to convert it before using"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Is the reference data resource versioned?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Many reference data sets evolve over time. If the reference data set changes, this may affect your results. If different versions of a reference data set exist, you need to establish your \"version policy\".</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",DetailedOption NoNumbering "Yes" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [TextFI {tfiDescriptor = FIDescriptor {iLabel = Just "Which version will you use?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "If there are different versions available, you have to decide with all project partners together which version you will be using. Probably you will go for the latest release as of the date of the start of your research project. However, if you have other data from older projects that need to be merged, you may need to consider using the same release you used for a previous project.", iLink = Nothing, iMandatory = True, iRules = []}}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you change version if it updates?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">If the reference changes while you are working on your research project, you need to decide whether you will follow these changes. Most likely that will mean that you have to do some analyses again, so you will need to make sure enough resources are available to do so. You can decide to stay with the version that you started with; this can have the disadvantage that you will not benefit from added information or added consistency.</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "Will stay with the old version",SimpleOption "New analyses will be done with the new version",SimpleOption "All analyses will be redone with the new version"]}]}]}]]}]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Just "What existing non-reference data sets will you use?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Even if you will be producing your own data, you often will also be relying on existing data sets. You may need to integrate your new data with an existing data set or retrieve additional information from related data bases. Will you be doing such things?</p>", iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [TextFI {tfiDescriptor = FIDescriptor {iLabel = Just "Items", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Just "Write each item on new line", iLongDescription = Just "<p class=\"question-description\">Even if you will be producing your own data, you often will also be relying on existing data sets. You may need to integrate your new data with an existing data set or retrieve additional information from related data bases. Will you be doing such things?</p>", iLink = Nothing, iMandatory = False, iRules = []}}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will the owners of this data set work with you on this study", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes",SimpleOption "We are the owners"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Is extenstion of any consent for privacy sensitive data be needed?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">If the data that you will re-use is coupled to people, is the informed consent that was originally obtained from those people covering your current research?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "Not applicable",SimpleOption "Existing consent applies",SimpleOption "New consent needed"]}]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you couple existing (biobank) data sets?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",DetailedOption NoNumbering "Yes" [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you use deterministic couplings?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Data sets that have exactly identical fields that are well filled can be coupled using deterministic methods. Will you be using such methods?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [TextFI {tfiDescriptor = FIDescriptor {iLabel = Just "Will you be using a trusted third party for coupling?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "What will be the procedure that is followed? Where will what data be sent? Did a legal advisor look at the procedures?", iLink = Nothing, iMandatory = True, iRules = []}}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Is consent available for the couplings?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [TextFI {tfiDescriptor = FIDescriptor {iLabel = Just "How will you check whether your coupled data are representative of your goal population?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "Sometimes, through the nature of the data sets that are coupled, the coupled set is no longer representative for the whole population (e.g. some fields may only have been filled for people with high blood pressure). This can disturb your research if undetected. How will you make sure this is not happening?", iLink = Nothing, iMandatory = True, iRules = []}}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "What is the goal of the coupling?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "More data about the same subjects (intersection)",SimpleOption "Enlarging the group of subjects (union)"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [TextFI {tfiDescriptor = FIDescriptor {iLabel = Just "What variable(s) will you be using for the coupling?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you use probabilistic couplings?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">Data sets that have similar but not identical fields or with identical fields that are not consistently filled can be coupled using probabilistic methods. Will you be using such methods?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]}]}]]}]}]}]]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Do you need to harmonize different sources of existing data?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">If you are combining data from different sources, harmonization may be required. You may need to re-analyse some original data.</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]}]}]}]]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will reference data be created?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you be storing samples?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you be collecting experimental data?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]}]},Chapter {chDescriptor = FIDescriptor {iLabel = Just "Data design and planning", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Just "chapter", iLongDescription = Just "In the data design and planning phase, we will make sure that we know what data comes when, that we have enough storage space and compute power to deal with it, and that all the responsibilities have been taken care of.", iLink = Nothing, iMandatory = False, iRules = []}, chItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Are you using data types used by others too?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Will you be using new types of data?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "How will you be storing metadata?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">For the re-usability of your data by yourself or others at a later stage, a lot of information about the data, how it was collected and how it can be used should be stored with the data. Such data about the data is called metadata, and this set of questions are about this metadata</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "Skip",SimpleOption "Explore"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Do you need to do storage capacity planning?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">If you collect substantial amounts of data, amounts that do not trivially fit on the storage that you normally have available or that can not be trivially transported between systems, some planning is necessary. Do you think you need to do storage capacity planning? If the expected total data volume is larger than 1 TB, you probably need to answer 'yes' here.</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Is the risk of information loss or theft acceptable?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">There are many factors that can contribute to the risk of information loss or information theft. They are often part of the behavior of the people that are involved in the project, but can also be steered by properly planned infrastructure.</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "Yes",SimpleOption "Drill deeper"]}]},SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Do you need to do compute capacity planning?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Just "<p class=\"question-description\">If you require substantial amounts of compute power, amounts that are not trivially absorbed in what you usually have abailable, some planning is necessary. Do you think you need to do compute capacity planning?</p>", iLink = Nothing, iMandatory = True, iRules = []}, chfiAvailableOptions = [SimpleOption "No",SimpleOption "Yes"]}]}]},Chapter {chDescriptor = FIDescriptor {iLabel = Just "Data Capture (the equipment phase)", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Just "chapter", iLongDescription = Nothing, iLink = Nothing, iMandatory = False, iRules = []}, chItems = [SimpleGroup {sgDescriptor = FIDescriptor {iLabel = Nothing, iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription = Nothing, iLink = Nothing, iMandatory = True, iRules = []}, sgLevel = 0, sgItems = [ChoiceFI {chfiDescriptor = FIDescriptor {iLabel = Just "Who will do the measurements?", iNumbering = NoNumbering, iIdent = Nothing, iTags = [], iShortDescription = Nothing, iLongDescription =